import { Component } from '@angular/core';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {

  constructor(public auth: AuthService) {}

  handleLogout(): void {

  }

}
<header [ngClass]="'container header_home'">
  <span id="logo">
    <img [src]="'./assets/logo3.png'" alt="logo" />
    <h1 class="logo">Jobtail</h1>
  </span>
  <div class="container header_home">
    <button type="button" class="btn btn_secondary" (click)="handleLogout()">
      Logout
    </button>
  </div>
  <div class="container-fluid">
    <div class="row">
      <div class="col-3" id="wishlist">Wishlist</div>
      <div class="col-3" id="applied">Applied</div>
      <div class="col-3" id="interview">Interview</div>
      <div class="col-3" id="declined">Declined</div>
    </div>
  </div>
</header>
