import { Component } from '@angular/core';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  template: '<button (click)="handleLogin()">Log in</button><button (click)="handleSignUp()">Sign up</button>',
})
export class HomeComponent {
  constructor(public auth: AuthService) {}

  handleLogin(): void {
    this.auth.loginWithRedirect({
      appState: {
        target: '/dashboard',
      },
    });
  }

  handleSignUp(): void {
    this.auth.loginWithRedirect({
    appState: {
      target: '/profile',
    },
    //screen_hint: '/dashboard',
  });
  }
}
<header [ngClass]="'container header_home'">
  <span id="logo">
    <img [src]="'./assets/logo3.png'" alt="logo" />
    <h1 class="logo">Jobtail</h1>
  </span>
  <div class="container header_home">
    <button
      type="button"
      class="btn btn_primary"
      id="Login"
      (click)="handleLogin()"
    >
      Login
    </button>
    <button type="button" class="btn btn_secondary" (click)="handleSignUp()">
      Sign Up
    </button>
  </div>
</header>
<main class="container">
  <div class="container">
    <h2>Stay on your applications’ tail</h2>
    <img [src]="'./assets/mac.png'" alt="job search" />
  </div>
  <div class="container">
    <button type="button" class="btn btn_secondary" (click)="handleSignUp()">
      Sign up for free
    </button>
    <p >Login to your account</p>
  </div>
</main>
