import { Component, Inject } from '@angular/core';
import { DOCUMENT } from '@angular/common';
import { AuthService } from '@auth0/auth0-angular';
import { CdkDragDrop, moveItemInArray, transferArrayItem } from '@angular/cdk/drag-drop';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {

  constructor(public auth?: AuthService, private modalService?: NgbModal, @Inject(DOCUMENT) private doc?: Document) {
    this.modalService = modalService;

  }

  public open(modal: any): void {
    this.modalService!.open(modal);
  }

  // TODO: Replace with actual data
  // Hook up to MongoDB
  wishlistJobs: any[] = [];
  appliedJobs: any[] = [];
  interviewJobs: any[] = [];
  offerJobs: any[] = [];
  declinedJobs: any[] = [];

  handleLogout(): void {
    this.auth!.logout({ returnTo: this.doc!.location.origin });
  }

  addNewJob(): void {

  }

  drop(event: CdkDragDrop<string[]>) {
    if (event.previousContainer === event.container) {
      moveItemInArray(event.container.data, event.previousIndex, event.currentIndex);
    } else {
      transferArrayItem(
        event.previousContainer.data,
        event.container.data,
        event.previousIndex,
        event.currentIndex,
      );
    }

  }


}
<header [ngClass]="'container header_home'">
  <span id="logo">
    <img [src]="'./assets/logo.png'" alt="logo" />
    <h1 class="logo">Jobtail</h1>
  </span>
  <div class="container header_home">
    <h5 class="header">Hello, user!</h5>
    <button type="button" class="btn btn_primary" (click)="handleLogout()">
      Logout
    </button>
  </div>
</header>

<main>
  <div class="container main_body">
    <div class="row">
      <div class="col-3">
        <div class="col bg-secondary" style="margin-right: 5%">
          <h2>Wishlist</h2>
          <button (click)="addNewJob()">Add New Job</button>
          <div class="job-card" *ngFor="let job of wishlistJobs" cdkDrag>
            {{ job.name }} - {{ job.employer }}
          </div>
        </div>
      </div>
      <div class="col-3">
        <div class="col bg-secondary" style="margin-right: 5%">
          <h2>Applied</h2>
          <div class="job-card" *ngFor="let job of appliedJobs" cdkDrag>
            {{ job.name }} - {{ job.employer }}
          </div>
        </div>
      </div>
      <div class="col-3">
        <div class="col bg-secondary" style="margin-right: 5%">
          <h2>Interview</h2>
          <div class="job-card" *ngFor="let job of interviewJobs" cdkDrag>
            {{ job.name }} - {{ job.employer }}
          </div>
        </div>
      </div>
      <div class="col-3">
        <div class="col bg-secondary">
          <h2>Declined</h2>
          <div class="job-card" *ngFor="let job of declinedJobs" cdkDrag>
            {{ job.name }} - {{ job.employer }}
          </div>
        </div>
      </div>
    </div>
  </div>
</main>
