import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {

  ngOnInit(): void {
    this.addEvent();
  }

  draggables = document.querySelectorAll('.draggable');
  containers = document.querySelectorAll('.container');

  addEvent() {
    this.draggables.forEach((draggable) => {
      draggable.addEventListener("dragstart", () => {
        draggable.classList.add("dragging");
      });

      draggable.addEventListener("dragend", () => {
        draggable.classList.remove("dragging");
      });
    });

    this.containers.forEach((category) => {
      category.addEventListener("dragover", (e) => {
        e.preventDefault();
        const afterElement = this.getDragAfterElement(category, e.clientY);
        const draggable = document.querySelector(".dragging");
        let id = draggable.id;
        if (afterElement == null) {
          category.appendChild(draggable);
          this.data[id].categories = `${category.id}`;
          localStorage.setItem("data", JSON.stringify(this.data));
        } else {
          category.insertBefore(draggable, afterElement);
          this.data[id].categories = `${category.id}`;
          localStorage.setItem("data", JSON.stringify(this.data));
        }
      });
    });
  }

  getDragAfterElement(category: HTMLElement, y: number): HTMLElement {
    const draggableElements = Array.from(category.querySelectorAll(".draggable:not(.dragging)"));

    return draggableElements.reduce(
      (closest, child) => {
        const box = child.getBoundingClientRect();
        const offset = y - box.top - box.height / 2;
        if (offset < 0 && offset > closest.offset) {
          return { offset, element: child as HTMLElement };
        } else {
          return closest;
        }
      },
      { offset: Number.NEGATIVE_INFINITY }
    ).element;
  }

  form = document.getElementById("form") as HTMLFormElement;
  textInput = document.getElementById("textInput") as HTMLInputElement;
  companyInput = document.getElementById("companyInput") as HTMLInputElement;
  const dateInput = document.getElementById("dateInput") as HTMLInputElement;
  const textarea = document.getElementById("textarea") as HTMLTextAreaElement;
  const categories = document.getElementById("categories") as HTMLSelectElement;
  const category = document.querySelector(".category") as HTMLDivElement;
  const add = document.getElementById("add") as HTMLButtonElement;
  const jobNum = 0;

}
<!DOCTYPE html>
<html lang="en">
  <head>
    <title>Joptail</title>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1" />

    <link
      href="https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/css/bootstrap.min.css"
      rel="stylesheet"
      integrity="sha384-1BmE4kWBq78iYhFldvKuhfTAU6auU8tT94WrHftjDbrCEXSU1oBoqyl2QvZ6jIW3"
      crossorigin="anonymous"
    />

    <script
      src="https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/js/bootstrap.bundle.min.js"
      integrity="sha384-ka7Sk0Gln4gmtz2MlQnikT1wXgYsOg+OMhuP+IlRH9sENBO0LRn5q+8nbTov4+1p"
      crossorigin="anonymous"
    ></script>

    <link
      rel="stylesheet"
      href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css"
    />
    <script src="../../scripts/script.js" defer></script>
    <link rel="icon" href="./logo2.jpg" type="image/x-icon" />

    <meta name="viewport" content="width=device-width, initial-scale=1" />
    <meta
      name="viewport"
      content="width=device-width, initial-scale=1, maximum-scale=1, user-scalable=no"
    />
  </head>

  <body>
    <header class="container-fluid">
      <span id="logo">
        <img src="./logo.png" alt="logo" />
        <h1 class="logo">Jobtail</h1>
      </span>

      <a href="./login.html"
        ><button type="button" class="btn btn-light" id="Login">
          Log out
        </button></a
      >
    </header>

    <!-- Modal -->
    <form
      class="modal fade"
      id="form"
      tabindex="-1"
      aria-labelledby="exampleModalLabel"
      aria-hidden="true"
    >
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header">
            <h5 class="modal-title" id="exampleModalLabel">Add New Job</h5>
            <button
              type="button"
              class="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
            ></button>
          </div>
          <div class="modal-body">
            <p>Company</p>
            <input
              required
              type="text"
              class="form-control"
              name=""
              id="companyInput"
            />
            <br />
            <p>Job Title</p>
            <input
              required
              type="text"
              class="form-control"
              name=""
              id="textInput"
            />
            <br />
            <p>Due Date</p>
            <input type="date" class="form-control" name="" id="dateInput" />
            <br />
            <p>Description</p>
            <textarea
              name=""
              class="form-control"
              id="textarea"
              cols="30"
              rows="5"
            ></textarea>
            <br />
            <p>choose a category</p>
            <!-- <label  for="categories"> choose a category</label> -->
            <select class="form-select" name="category" id="categories">
              <option selected value="Wishlist">Wishlist</option>
              <option value="Applied">Applied</option>
              <option value="Interview">Interview</option>
              <option value="Declined">Declined</option>
              <option value="Offer">Offer</option>
            </select>
          </div>
          <div class="modal-footer">
            <button
              type="button"
              class="btn btn-secondary"
              data-bs-dismiss="modal"
            >
              Close
            </button>
            <button type="submit" id="add" class="btn btn-primary">Add</button>
          </div>
        </div>
      </div>
    </form>

    <main>
      <div id="Wishlist" class="category">
        <h2>Wishlist</h2>
        <div id="addNew" data-bs-toggle="modal" data-bs-target="#form">
          <span>Add New Job</span>
          <i class="fas fa-plus"></i>
        </div>
      </div>
      <div id="Applied" class="category">
        <h2>Applied</h2>
      </div>
      <div id="Interview" class="category">
        <h2>Interview</h2>
      </div>
      <div id="Declined" class="category">
        <h2>Declined</h2>
      </div>
      <div id="Offer" class="category">
        <h2>Offer</h2>
      </div>
    </main>
  </body>
</html>
