import { Component, Inject } from '@angular/core';
import { DOCUMENT } from '@angular/common';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  template: '<button (click)="handleLogin()">Log in</button><button (click)="handleSignUp()">Sign up</button>',
})
export class HomeComponent {

  constructor(public auth: AuthService, @Inject(DOCUMENT) private doc?: Document) {}

  handleLogin(): void {
    this.auth.loginWithRedirect({
      appState: {
        target: '/dashboard',
      },
      authorizationParams: {
        redirect_uri: 'http://localhost:4200/dashboard',
      }
    });
  }

  handleSignUp(): void {
    this.auth.loginWithRedirect({
    appState: {
      target: '/profile',
    },
    authorizationParams: {
      screen_hint: 'signup',
      redirect_uri: 'http://localhost:4200/dashboard',
    }
  });
  }

  handleLogout(): void {
    this.auth!.logout({ returnTo: this.doc!.location.origin });
  }
}
<header [ngClass]="'container header_home'">
  <span id="logo">
    <img [src]="'./assets/logo3.png'" alt="logo" />
    <h1 class="logo">Jobtail</h1>
  </span>
  <h5>{{ auth.isAuthenticated$ }}</h5>
  <div *ngIf="auth.isAuthenticated$ | async; else unAuthenticated">
    <div class="container header_home">
      <button
        type="button"
        class="btn btn_primary"
        id="Login"
        (click)="handleLogin()"
      >
        Login
      </button>
      <button type="button" class="btn btn_secondary" (click)="handleSignUp()">
        Sign Up
      </button>
    </div>
  </div>
  <ng-template #unAuthenticated>
    <div class="container header_home">
      <button type="button" class="btn btn_secondary" (click)="handleLogout()">
        Logout
      </button>
    </div>
  </ng-template>
</header>
<main class="container">
  <div class="container">
    <h2>Stay on your applications’ tail</h2>
    <img [src]="'./assets/mac.png'" alt="job search" />
  </div>
  <div class="container">
    <button type="button" class="btn btn_secondary" (click)="handleSignUp()">
      Sign up for free
    </button>
    <p (click)="handleLogin()">Login to your account</p>
  </div>
</main>
