import { Component } from '@angular/core';
import { AuthService } from '@auth0/auth0-angular';
import {CdkDragDrop, DragDropModule, moveItemInArray, transferArrayItem} from '@angular/cdk/drag-drop';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {

  wishlistJobs: any[] = [];
  appliedJobs: any[] = [];
  interviewJobs: any[] = [];
  offerJobs: any[] = [];
  declinedJobs: any[] = [];

  constructor(public auth: AuthService) {}

  handleLogout(): void {

  }

  addNewJob(): void {

  }

  drop(event: CdkDragDrop<string[]>) {
    if (event.previousContainer === event.container) {
      moveItemInArray(event.container.data, event.previousIndex, event.currentIndex);
    } else {
      transferArrayItem(
        event.previousContainer.data,
        event.container.data,
        event.previousIndex,
        event.currentIndex,
      );
    }

  }


}
<header [ngClass]="'container header_home'">
  <span id="logo">
    <img [src]="'./assets/logo.png'" alt="logo" />
    <h1 class="logo">Jobtail</h1>
  </span>
  <div class="container header_home">
    <button type="button" class="btn btn_primary" (click)="handleLogout()">
      Logout
    </button>
  </div>
</header>

<main>
  <div cdkDropListGroup>
    <div class="example-container">
      <h2>Available items</h2>

      <div
        cdkDropList
        [cdkDropListData]="items"
        class="example-list"
        cdkDropListSortingDisabled
        (cdkDropListDropped)="drop($event)"
      >
        <div class="example-box" *ngFor="let item of items" cdkDrag>
          {{ item }}
        </div>
      </div>
    </div>

    <div class="example-container">
      <h2>Shopping basket</h2>

      <div
        cdkDropList
        [cdkDropListData]="basket"
        class="example-list"
        (cdkDropListDropped)="drop($event)"
      >
        <div class="example-box" *ngFor="let item of basket" cdkDrag>
          {{ item }}
        </div>
      </div>
    </div>
  </div>
</main>
